import jsPDF from "jspdf";
import * as htmlToImage from "html-to-image";

export async function exportCrimeDataToPdf() {
  const doc = new jsPDF("p", "px");
  const pageWidth = doc.internal.pageSize.getWidth();
  const pageHeight = doc.internal.pageSize.getHeight();
  const padding = 10; // Adjust padding as needed
  const graphWidth = (pageWidth - 14 * padding); // Four graphs in a row
  const graphHeight = (pageHeight-15*padding)/4; // Set the desired height for each graph

  const elements = document.getElementsByClassName("custom-chart");


  for (let i = 0; i < elements.length; i) {
      // doc.addPage(); // Add a new page for every four graphs
    const img="data:image/png;base64,";
    doc.addImage(img, "PNG", padding, padding, 92, 16);

    for(let j=0; j<4 && j+i<elements.length ; j++, i++){
      const el = elements[i] as HTMLElement;
      const imgData = await htmlToImage.toPng(el);

      const x = 7*padding;
      const y = j * (graphHeight + padding) + 5*padding;

      doc.addImage(imgData, "PNG", x, y, graphWidth, graphHeight);
    }

    if(i<elements.length)
      doc.addPage();
  }

  doc.save("crime_data.pdf");
}
